<script setup>
import { ref, inject } from "vue";

const props = defineProps({
    title: String,
    date: String,
    selected: Boolean,
});

const select_ = inject("select");
const select = () => {
    select_(`${props.title}-${props.date}`);
};

const remove_ = inject("remove");
